mapper.select:
select *
from cms_admin_info
where lgn_id = #{lgn_id} and lgn_passwd = #{lgn_passwd}
limit 1;

mapper.delete:
DELETE FROM cms_board_mng
WHERE tree_id IN

    #{id}

mapper.select:
SELECT *
FROM cms_board_info
where bbs_id = #{bbs_id}
LIMIT 1;

SELECT *
FROM cms_file
WHERE  file_id = #{file_id}
limit 1;

select *
from cms_board_mng cbm
WHERE tree_id = #{tree_id}
limit 1;

SELECT *
FROM cms_content_info
WHERE  content_id = #{content_id}
limit 1;

select file_id
from cms_file_link cfl
where content_id = #{content_id}

SELECT *
FROM cms_content_cat
where cat_id = #{cat_id}
limit 1;

SELECT *
FROM cms_content_cat
where bbs_id = #{bbs_id}
order by cat_id;

mapper.update:
UPDATE cms_content_cat
SET
cat_label = #{cat_label}
WHERE
cat_id = #{cat_id}

mapper.select:
SELECT order_idx
FROM cms_content_info
WHERE content_id = #{contentId}
LIMIT 1;

mapper.update:
UPDATE cms_content_info
SET order_idx = #{orderIdx}
WHERE content_id = #{contentId}

update.foreach:
UPDATE cms_content_info
SET order_idx = #{item.order_idx}
WHERE content_id = #{item.content_id}
