mapper.select:
select *
from cms_admin_info
where lgn_id = #{lgn_id} and lgn_passwd = #{lgn_passwd}
limit 1;

mapper.delete:
DELETE FROM cms_board_mng
WHERE tree_id IN

    #{id}

mapper.select:
SELECT *
FROM cms_board_info
where bbs_id = #{bbs_id}
LIMIT 1;

SELECT
    a.*, c.file_id , c.file_path
FROM  cms_content_info a  LEFT JOIN cms_file_link b ON a.content_id = b.content_id left join cms_file c on b.file_id = c.file_id
WHERE  a.bbs_id = #{bbs_id}
ORDER BY  a.reg_dt DESC;

SELECT *
FROM cms_file
WHERE  file_id = #{file_id}
limit 1;

select *
from cms_board_mng cbm
WHERE tree_id = #{tree_id}
limit 1;

SELECT *
FROM cms_content_info
WHERE  content_id = #{content_id}
limit 1;

select file_id
from cms_file_link cfl
where content_id = #{content_id}
